import React from 'react';
import './SongDisplay.css';
import { useLocation } from 'react-router-dom';


function SongDisplay() {
  const location = useLocation();
  const { songTitle, focusSong, language, subject, range, rhythmicElements, tonalCenter, scale, toneSet, ethnicOrigin, gameDirections } = location.state;

  return (
    <div className="songDisplay">
      <h1>{songTitle}</h1>
      <p>language: {language}</p>
      <p>focus song: {focusSong}</p>
      <p>subject: {subject}</p>
      <p>tone set: {toneSet}</p>
      <p>scale: {scale}</p>
      <p>game directions: {gameDirections}</p>
    </div>
  );
}

export default SongDisplay;
