import React from 'react';
import './SongDisplay.css';
import { useLocation, useNavigate } from 'react-router-dom';


function SongDisplay() {
  const location = useLocation();
  const { songTitle, focusSong, language, subject, range, rhythmicElements, tonalCenter, scale, toneSet, ethnicOrigin } = location.state;
  const navigate = useNavigate();

  useEffect(() => {
    const handlePopState = () => {
      navigate(-1); // Navigate back to the previous page (Home component) when the popstate event occurs
    };

    window.addEventListener('popstate', handlePopState);

    return () => {
      window.removeEventListener('popstate', handlePopState);
    };
  }, [navigate]);

  return (
    <div className="songDisplay">
      <h1>{songTitle}</h1>
      <p>language: {language}</p>
      <p>focus song: {focusSong}</p>
      <p>subject: {subject}</p>
      <p>tone set: {toneSet}</p>
      <p>scale: {scale}</p>
    </div>
  );
}

export default SongDisplay;
