import React from 'react';
import './SongDisplay.css';
import { useLocation } from 'react-router-dom';
import testImage from '../../songImages/aLaClaireFontaine.png'
import aLaClaireFontaineImage from '../../songImages/aLaClaireFontaine.png';


function SongDisplay() {
  const location = useLocation();
  const songData = location.state;
  console.log(songData);

  return (
    <div className="songDisplay">
      <h1>{songData.songData.songTitle}</h1>
      <p>{songData.songData.score}</p>
      {songData.score && (
        <>
          <p>Score:</p>
          <img src={songData.score} alt="Score" />
        </>
      )}
      <img src= "../../songImages/aLaClaireFontaine.png" />
      <img src={aLaClaireFontaineImage} alt="aLaClaireFontaine" />
      <img src={testImage} />
      <p>language: {songData.songData.language}</p>
      <p>CSP: {songData.songData.CSP}</p>
      <p>focus song: {songData.songData.focusSong}</p>
      <p>subject: {songData.songData.subject}</p>
      <p>tone set: {songData.songData.toneSet}</p>
      <p>scale: {songData.songData.scale}</p>
      <p>game directions: {songData.songData.gameDirections}</p>
      <p>source: {songData.songData.source}</p>
    </div>
  );
}

export default SongDisplay;
