import React from 'react';
import './Home.css';
import repDir from '../../repGood.json';
import { useState } from 'react';
import { NavLink } from 'react-router-dom';

function Home() {
  const searchCriteria = [
    'language',
    'scale',
    'focusSong',
    'subject',
    'tonalCenter',
    'ethnicOrigin',
    // 'toneSet',
    'rhythmicElements',
    'range',
  ];

  const toneSetOptions = ['sol', 'la', 'ti', 'do', 're', 'mi', 'fa'];

  // STATE VARIABLES

  const [filters, setFilters] = useState({});
  const [gradeFilter, setGradeFilter] = useState([]);
  const [toneSetFilter, setToneSetFilter] = useState([]);

  // FILTERS THE SONGS BASED ON THE SELECTED CRITERIA
  const filteredSongs = repDir.filter(
    (song) =>
      Object.keys(filters).every((criteria) =>
        filters[criteria] ? song[criteria] === filters[criteria] : true
      ) &&
      (gradeFilter.length === 0 ||
        (song.grade && song.grade.some((grade) => gradeFilter.includes(grade)))) &&
        (toneSetFilter.length === 0 || toneSetFilter.every((tone) => song.toneSet.includes(tone)))

  );

  // EVENT HANDLERS
  const handleFilterChange = (criteria, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [criteria]: value !== '' ? value : null,
    }));
  };

  const handleGradeChange = (value) => {
    if (value === 'all') {
      setGradeFilter([]);
    } else {
      if (gradeFilter.includes(value)) {
        setGradeFilter((prevFilter) =>
          prevFilter.filter((grade) => grade !== value)
        );
      } else {
        setGradeFilter((prevFilter) => [...prevFilter, value]);
      }
    }
  };

  const handleToneSetChange = (tone) => {
    if (toneSetFilter.includes(tone)) {
      setToneSetFilter((prevFilter) => prevFilter.filter((t) => t !== tone));
    } else {
      setToneSetFilter((prevFilter) => [...prevFilter, tone]);
    }
  };


  const handleResetClick = () => {
    setFilters({});
    setGradeFilter([]);
  };

  return (
    <div className="home">
      <div className="parent">
        <div className="div1">
          <p>
            Showing <em>{filteredSongs.length}</em> of <em>{repDir.length}</em>{' '}
            songs in the collection.
          </p>
        </div>

        <div className="div2">
          <h3>refine search</h3>
          <button onClick={handleResetClick}>reset</button>


          <p>grade</p>
          <label>
            <input
              type="checkbox"
              value="all"
              checked={!gradeFilter.length}
              onChange={() => setGradeFilter([])}
            />
            All
          </label>
          {Array.from({ length: 6 }, (_, index) => index).map((grade) => (
            <label key={grade}>
              <input
                type="checkbox"
                value={grade}
                checked={gradeFilter.includes(grade)}
                onChange={() => handleGradeChange(grade)}
              />
              {grade}
            </label>
          ))}

          {searchCriteria.map((criteria) => (
            <React.Fragment key={criteria}>
              <p>{criteria}</p>
              <select
                id={`${criteria}Filter`}
                value={filters[criteria] || ''}
                onChange={(e) => handleFilterChange(criteria, e.target.value)}
              >
                <option value="">All</option>
                {Array.from(new Set(repDir.map((song) => song[criteria]))).map(
                  (option) => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  )
                )}
              </select>
            </React.Fragment>
          ))}
        </div>

        <div className="div4">
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Recording</th>
                  <th>Game</th>
                </tr>
              </thead>
              <tbody>
                {filteredSongs.map((song) => (
                  <tr key={song.id}>
                    <td>
                      <NavLink
                        state={{
                          // songTitle: song.songTitle,
                          // language: song.language,
                          // focusSong: song.focusSong,
                          // subject: song.subject,
                          // scale: song.scale,
                          // tonalCenter: song.tonalCenter,
                          // ethnicOrigin: song.ethnicOrigin,
                          // toneSet: song.toneSet,
                          // range: song.range,
                          // rhythmicElements: song.rhythmicElements,
                          // gameDirections: song.gameDirections
                          songData: song,
                        }}
                        to={{ pathname: '/songDisplay' }}
                      >
                        {song.songTitle}
                      </NavLink>
                    </td>
                    <td>{song.recording ? 'x' : null}</td>
                    <td>{song.game ? 'x' : null}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
