import React from 'react';
import './Home.css';
import repDir from '../../repGood.json';
import { useState } from 'react';

function Home() {
  const searchCriteria = [
    'language',
    'scale',
    'focusSong',
    'subject',
    'tonalCenter',
    'ethnicOrigin',
    'rhythmicElements',
    'range',
    ...repDir.reduce((criteria, song) => criteria.concat(song.toneSet), [])
    .filter((value, index, self) => self.indexOf(value) === index) // Remove duplicates
  ];

  const [filters, setFilters] = useState({});
  const [gradeFilter, setGradeFilter] = useState([]); 


  // FILTERS THE SONGS BASED ON THE SELECTED CRITERIA
  const filteredSongs = repDir.filter((song) =>
  Object.keys(filters).every((criteria) =>
    filters[criteria] ? song[criteria] === filters[criteria] : true
  ) &&
  (gradeFilter.length === 0 || (song.grade && song.grade.some((grade) => gradeFilter.includes(grade)))) &&
  (!filters.toneSet || filters.toneSet.every((tone) => song.toneSet && song.toneSet.includes(tone)))
);

  // EVENT HANDLERS
  const handleFilterChange = (criteria, value) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [criteria]: value !== '' ? value : null,
    }));
  };

    const handleGradeChange = (value) => {
      if (value === "all") {
        setGradeFilter([]);
      } else {
        if (gradeFilter.includes(value)) {
          setGradeFilter((prevFilter) =>
            prevFilter.filter((grade) => grade !== value)
          );
        } else {
          setGradeFilter((prevFilter) => [...prevFilter, value]);
        }
      }
    };

    const handleToneSetChange = (e) => {
      const tone = e.target.value;
      const updatedToneSet = filters.toneSet ? [...filters.toneSet] : [];

      if (e.target.checked) {
        updatedToneSet.push(tone);
      } else {
        const index = updatedToneSet.indexOf(tone);
        if (index !== -1) {
          updatedToneSet.splice(index, 1);
        }
      }

      return updatedToneSet.length > 0 ? updatedToneSet : null;
    };

  const handleResetClick = () => {
    setFilters({});
    setGradeFilter([]);
  };

  return (
    <div className="home">
      <div className="parent">
        <div className="div1">
          <p>
            Showing <em>{filteredSongs.length}</em> of <em>{repDir.length}</em>{' '}
            songs in the collection.
          </p>
        </div>

        <div className="div2">
          <h3>refine search</h3>
          <button onClick={handleResetClick}>reset</button>
          <p>grade</p>
  <label>
    <input
      type="checkbox"
      value="all"
      checked={!gradeFilter.length}
      onChange={() => setGradeFilter([])}
    />
    All
  </label>
  {Array.from({ length: 6 }, (_, index) => index).map((grade) => (
    <label key={grade}>
      <input
        type="checkbox"
        value={grade}
        checked={gradeFilter.includes(grade)}
        onChange={() => handleGradeChange(grade)}
      />
      {grade}
    </label>
  ))}

<p>toneSet</p>
{Array.from({ length: 8 }, (_, index) => index).map((toneIndex) => {
  const tone = ['do', 're', 'mi', 'fa', 'sol', 'la', 'ti', 'do'][toneIndex];
  return (
    <label key={tone}>
      <input
        type="checkbox"
        value={tone}
        checked={filters.toneSet && filters.toneSet.includes(tone)}
        onChange={(e) => handleFilterChange('toneSet', handleToneSetChange(e))}
      />
      {tone}
    </label>
  );
})}


          {searchCriteria.map((criteria) => (
            <React.Fragment key={criteria}>
              <p>{criteria}</p>
              <select
                id={`${criteria}Filter`}
                value={filters[criteria] || ''}
                onChange={(e) => handleFilterChange(criteria, e.target.value)}
              >
                <option value="">All</option>
                {Array.from(new Set(repDir.map((song) => song[criteria]))).map(
                  (option) => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  )
                )}
              </select>
            </React.Fragment>
          ))}
        </div>

        <div className="div4">
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Recording</th>
                  <th>Game</th>
                </tr>
              </thead>
              <tbody>
                {filteredSongs.map((song) => (
                  <tr key={song.id}>
                    <td>{song.songTitle}</td>
                    <td>{song.recording ? 'x' : null}</td>
                    <td>{song.game ? 'x' : null}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
