import './LessonDisplay.css';
import { useLocation } from 'react-router-dom';
import repDir from '../../repGood2.json';
import { shuffleArray, selectRandomItems } from '../../utilities';
import { useSelector } from 'react-redux';

function LessonDisplay() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const selectedMelodicConcept = searchParams.get('melodicConcept');
  const selectedSongsInHolding = useSelector((state) => state.selectedSongs.selectedSongs);

  // SEARCHES FOR SONG TITLE THAT CAN PRESENT CONCEPT
  const findFocusSong = (concept, selectedSongs) => {
    const matchingSongs = [];
    for (const song of repDir) {
      if (song.function.melodic.presentation.includes(concept)) {
        matchingSongs.push(song.songTitle);
      }
    }
    if (matchingSongs.length === 0) {
      return 'No matching song found.  Go on a repertoire hunt!';
    }
    return selectRandomItems(matchingSongs, 1)[0];
  };

  const focusSongTitle = findFocusSong(selectedMelodicConcept, selectedSongsInHolding);

  // SEARCHES FOR LIST OF PRACTICE SONGS FOR SELECTED CONCEPT
  const findMatchingPracticeSongs = (concept, focusSong, selectedSongs) => {
    const matchingSongs = [];
    for (const songTitle of selectedSongs) {

    }

    for (const song of repDir) {
      if ((
        song.function.melodic.practice.includes(concept) || selectedSongs.includes(song.songTitle)) &&
        song.songTitle !== focusSongTitle
      ) {
        matchingSongs.push(song.songTitle);
      }
    }

    // COMBINES MATCHING SONGS WITH THOSE IN HOLDING DRAWER
    const allMatchingSongs = [...matchingSongs, ...selectedSongs];
    return selectRandomItems(allMatchingSongs, 5);
  };

  const practiceSongTitles = findMatchingPracticeSongs(selectedMelodicConcept, focusSongTitle, selectedSongsInHolding);

  // const matchingSongs = findMatchingPracticeSongs(selectedMelodicConcept);
  // const randomizedPracticeSongs = shuffleArray(
  //   selectRandomItems(matchingSongs, 5)
  // );

  return (
    <div className="lessonDisplay">
      <h1>LESSON DISPLAY</h1>
      <h2>Concept:</h2>
      <p>{selectedMelodicConcept}</p>
      <h3>Focus rep:</h3>
      <p>{focusSongTitle}</p>
      <h3>Practice rep:</h3>
      <ul className="practiceRep">
        {practiceSongTitles.map((title, index) => (
          <li key={index}>{title}</li>
        ))}
      </ul>
    </div>
  );
}

export default LessonDisplay;
