import './Home.css';
import repDir from '../../repGood.json';
import { useState } from 'react';

function Home() {

  // STATE VARIABLES
  const [languageFilter, setLanguageFilter] = useState('');
  const [scaleFilter, setScaleFilter] = useState('');
  // const [displayList, changeDisplayList] = useState(repDir);

  // FILTERS THE SONGS BASED ON LANGUAGE CRITERIA
  const filteredSongs = repDir.filter((song) => {
    languageFilter ? song.language === languageFilter : true;
    const scaleMatch = scaleFilter ? song.scale === scaleFilter : true;
  }
  );

  return (
    <div className="home">
      <div className="parent">
        <div className="div1">
          <p>
            Showing <em>270</em> of <em>{repDir.length}</em> songs in the
            collection.
          </p>
        </div>

        <div className="div2">
          <p>refine search</p>
          <p>reset</p>

          <p>scale</p>
          <select
            id="scaleFilter"
            value={scaleFilter}
            onChange={(e) => setScaleFilter(e.target.value)}
          >
            <option value="">All</option>
            {Array.from(new Set(repDir.map((song) => song.scale))).map(
              (scale) => (
                <option key={scale} value={scale}>
                  {scale}
                </option>
              )
            )}
          </select>

          <p>language</p>
          <select
            id="languageFilter"
            value={languageFilter}
            onChange={(e) => setLanguageFilter(e.target.value)}
          >
            <option value="">All</option>
            {Array.from(new Set(repDir.map((song) => song.language))).map(
              (language) => (
                <option key={language} value={language}>
                  {language}
                </option>
              )
            )}
          </select>
        </div>

        <div className="div4">
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Recording</th>
                  <th>Game</th>
                </tr>
              </thead>
              <tbody>
                {filteredSongs.map((song) => (
                  <tr key={song.id}>
                    <td>{song.songTitle}</td>
                    <td>{song.recording ? 'x' : null}</td>
                    <td>{song.game ? 'x' : null}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
